<?php

// Simple string
"This is a double quoted string literal";
// String with common escape sequences
"\\ \" \$ \e \f \n \r \t \v";
// String with octal escape sequences
"\7 \77 \777";
// String with hexadecimal escape sequences
"\x8 \xFF";
// String with unicode escape sequences
"\u{91223} \u{00CCFF} \u{1}";
// String with literal special characters
"\$efnrtv";
// String with final backslash
"\\";
// Empty string 
"";
// String with variable
"variable: $foo";

?>